import React,{ Component } from 'react';
import { Card, CardImg, CardImgOverlay, 
  CardTitle,CardSubtitle,CardText,CardBody } from 'reactstrap';
  import { Breadcrumb, BreadcrumbItem,
    Button, Form, FormGroup, Label, Input, Col } from 'reactstrap';
  import {findDOMNode} from "react-dom" ;
  import $ from "jquery"
import { NONAME } from 'dns';
  function RanderItem({item}){
    console.log({item})
    return(
    <Card>
    <CardImg src={item.image}  width="50px" height="100px"alt="specialDish"></CardImg>
    
    <CardBody>
    
    
    <CardTitle>{item.name}</CardTitle>
                {item.designation ? <CardSubtitle>{item.designation}</CardSubtitle> : null }
                <CardText>{item.description}</CardText>
    </CardBody>
    </Card>
      )  }
class Home extends Component{
constructor(props){

    super(props)
    this.state={
        firstname:"",
        lastname:"",
        telnum:"",
        email:"",
        agree:"",
        contactType:"Tel.",
        touched:{
            firstname:"",
            lastname:"",
            email:"",
telnum:"",

        }
    }
    this.handleChange=this.handleChange.bind(this)
    this.handleSubmit=this.handleSubmit.bind(this)
    this.handleBlur=this.handleBlur.bind(this)
}
handleBlur(field){
    this.setState({...this.state.touched,[field]:true})
}
handleChange(event){
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
    var el=findDOMNode(this.refs.toggle)
    var el1=findDOMNode(this.refs.toggle1)
 if(value=="Email")
 {
 $(el).show()
 $(el1).hide()
 }
 else{
    $(el1).show()
    $(el).hide()
 }


  
    this.setState({
      [name]: value
    });
    console.log(value+5)
  }
  handleSubmit(event){
    console.log('Current State is: ' + JSON.stringify(this.state));
    alert('Current State is: ' + JSON.stringify(this.state));
    event.preventDefault();
  }
render(){

    return( 
        <div>
      <div className="container">
    <div className="row align-items-start">
    <RanderItem item={this.props.dish}>
    
    
    </RanderItem>
    
    </div>
    {console.log("test12")}
    <div className="row align-items-start">
    <RanderItem item={this.props.promo}>
    
    
    </RanderItem>
    </div>
    <div className="row align-items-start">
    <RanderItem item={this.props.leader}>
    
    
    </RanderItem>
    
    </div>
    <div className="row row-content"
    >
<div className="col-12">
<h1>send us your feedback</h1>

</div>
<div className="col-12 col-md-9">
<Form onSubmit={this.handleSubmit}>
<FormGroup className="row">
<Col md={3}>
<Label htmlFor="firstanme">
firstname

</Label>
</Col>
<Col md={9}>
<Input   type="text" id="firstname" name="firstname" onChange={this.handleChange}  value={this.state.firstname} placeholder="firstname">
onBlur={this.handleBlur("firstname")}
/></Input>
</Col>



</FormGroup>
<FormGroup className="row">
<Col md={3}>
<Label htmlfor="lastanme" md={3}>
lastname

</Label>
</Col>
<Col md={9}>
<Input md={9} type="text" id="lastname" name="lastname" onChange={this.handleChange}  value={this.state.lastname} placeholder="firstname">
onBlur={this.handleBlur("lastname")}
</Input>

</Col>

</FormGroup>

<FormGroup row>

<Col md={3}>
<Label htmlfor="telnum" md={3}>
telephone no.

</Label>
</Col>

<Col md={9}>
<Input md={9} type="text" id="telnum" name="telnum" style={{display:"none"}} ref="toggle1" onChange={this.handleChange}  value={this.state.telnum} placeholder="tel.no.">
onBlur={this.handleBlur("telnum")}
</Input>
</Col>



</FormGroup>

<FormGroup row>
<Col md={3}>
<Label htmlfor="email" md={3}>
Email

</Label>
</Col>
<Col md={9}>
<Input md={9} type="email" id="email" name="email" style={{display:"none"}} ref="toggle"onChange={this.handleChange}  value={this.state.email} placeholder="email">
onBlur={this.handleBlur("email")}
</Input>
</Col>


</FormGroup>
<FormGroup row>
                                <Col md={{size: 6, offset: 2}}>
                                    <FormGroup check>
                                        <Label check>
                                            <Input type="checkbox"
                                                name="agree"
                                                checked={this.state.agree}
                                                onChange={this.handleChange} /> {' '}
                                            <strong>May we contact you?</strong>
                                        </Label>
                                    </FormGroup>
                                </Col>
                                <Col md={{size: 3, offset: 1}}>
                                    <Input type="select" name="contactType"
                                            value={this.state.contactType}
                                            onChange={this.handleChange}>
                                        <option>Tel.</option>
                                        <option>Email</option>
                                    </Input>
                                </Col>
                            </FormGroup>



                            <FormGroup row>
                                <Col md={{size: 10, offset: 2}}>
                                    <Button type="submit" color="primary">
                                        Send Feedback
                                    </Button>
                                </Col>
                            </FormGroup>   
</Form>
</div>

    </div>
      </div>
      </div>
    );
    }
}

export default Home;
