import React from 'react';

function Home(props) {
  function renderItem({item}){
<card>
<cardImg src="{item.image}" alt="specialDish"></cardImg>

<cardBody>


<CardTitle>{item.name}</CardTitle>
            {item.designation ? <CardSubtitle>{item.designation}</CardSubtitle> : null }
            <CardText>{item.description}</CardText>
</cardBody>
</card>
  }

    return(
      <div className="container">
    <div className="row align-items-start">
    <randerItem item={props.dish}>
    
    
    </randerItem>
    <randerItem item={props.promotion}>
    
    
    </randerItem>
    <randerItem item={props.leader}>
    
    
    </randerItem>
    
    
    </div>
      </div>
    );
}

export default Home;
