import React,{ Component } from 'react';
import { Card, CardImg, CardImgOverlay, 
  CardTitle,CardSubtitle,CardText,CardBody } from 'reactstrap';
  import { Breadcrumb, BreadcrumbItem,
    Button, Form, FormGroup, Label, Input, Col } from 'reactstrap';
  function RanderItem({item}){
    console.log({item})
    return(
    <Card>
    <CardImg src={item.image}  width="50px" height="100px"alt="specialDish"></CardImg>
    
    <CardBody>
    
    
    <CardTitle>{item.name}</CardTitle>
                {item.designation ? <CardSubtitle>{item.designation}</CardSubtitle> : null }
                <CardText>{item.description}</CardText>
    </CardBody>
    </Card>
      )  }
class Home extends Component{
constructor(props){

    super(props)
    this.state={
        firstname:"",
        lastname:"",
        telnum:"",
        email:"",
        agree:"",
        contactType:"",
    }
}
handleChange(event){
    const target = event.target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    const name = target.name;
  
    this.setState({
      [name]: value
    });
    console.log(this.state.firstname)
  }
  handleSubmit(event){
    console.log('Current State is: ' + JSON.stringify(this.state.firstname));
    alert('Current State is: ' + JSON.stringify(this.state.firstaname));
    event.preventDefault();
  }
render(){

    return( 
      <div className="container">
    <div className="row align-items-start">
    <RanderItem item={this.props.dish}>
    
    
    </RanderItem>
    
    </div>
    {console.log("test12")}
    <div className="row align-items-start">
    <RanderItem item={this.props.promo}>
    
    
    </RanderItem>
    </div>
    <div className="row align-items-start">
    <RanderItem item={this.props.leader}>
    
    
    </RanderItem>
    
    </div>
    <div class="row row-content"
    >
<div class="col-12">
<h1>send us your feedback</h1>

</div>
<div class="col-12 col-md-9">
<Form onSubmit={this.handleSubmit}>
<FormGroup row>
<Label htmlFor="firstanme">
firstname

</Label>
<Input   type="text" id="firstname" name="firstname" onChange={this.handleChange}  value={this.state.firstname} placeholder="firstname">

</Input>



</FormGroup>
<FormGroup row>
<Label htmlfor="lastanme" md={3}>
lastname

</Label>
<Input md={9} type="text" id="lastname" name="lastname" onChange={this.handleChange}  value={this.state.lastname} placeholder="firstname">

</Input>



</FormGroup>

<FormGroup row>
<Label htmlfor="telnum" md={3}>
telephone no.

</Label>
<Input md={9} type="text" id="telnum" name="telnum" onChange={this.handleChange}  value={this.state.telnum} placeholder="tel.no.">

</Input>



</FormGroup>

<FormGroup row>
<Label htmlfor="email" md={3}>
Email

</Label>
<Input md={9} type="email" id="email" name="email" onChange={this.handleChange}  value={this.state.email} placeholder="email">

</Input>



</FormGroup>
<FormGroup row>
                                <Col md={{size: 6, offset: 2}}>
                                    <FormGroup check>
                                        <Label check>
                                            <Input type="checkbox"
                                                name="agree"
                                                checked={this.state.agree}
                                                onChange={this.handleChange} /> {' '}
                                            <strong>May we contact you?</strong>
                                        </Label>
                                    </FormGroup>
                                </Col>
                                <Col md={{size: 3, offset: 1}}>
                                    <Input type="select" name="contactType"
                                            value={this.state.contactType}
                                            onChange={this.handleInputChange}>
                                        <option>Tel.</option>
                                        <option>Email</option>
                                    </Input>
                                </Col>
                            </FormGroup>



                            <FormGroup row>
                                <Col md={{size: 10, offset: 2}}>
                                    <Button type="submit" color="primary">
                                        Send Feedback
                                    </Button>
                                </Col>
                            </FormGroup>   
</Form>
</div>

    </div>
      </div>
    );
    }
}

export default Home;
