import React,{ Component }  from 'react';


import 'bootstrap/dist/css/bootstrap.min.css';

import Menu from "./menu component"
import Menudetail from "./Menudetail"
import {dishes} from "../data/dish"
import Home from './HomeComponent';
import Header from "./header"
import Contact from "./contact"
import {LEADERS} from "../data/leader"
import {PROMOTIONS} from "../data/promotion"
import {COMMENTS} from "../data/comments"
import { Nav,  NavbarToggler, Collapse, NavItem, Jumbotron }from 'reactstrap'
import { Switch, Route, Redirect } from 'react-router-dom';

class Main extends Component {
  constructor(props){
    super(props)
    this.state={dishes:dishes,
      LEADERS:LEADERS,
      PROMOTIONS:PROMOTIONS,
      COMMENTS:COMMENTS,
      firstname:"",
      lastname:"",
      telnum:"",
      email:"",
      agree:"",
      contactType:"",

   
  }
console.log("maincomp constructor is invoked")
}

  render(){
    console.log("maincomp render is invoked")
    var homepage=()=>{
      console.log("test2")
   
      return(
       
        <Home dish={this.state.dishes.filter((dish)=>dish.featured)[0]}
        
        promo={this.state.PROMOTIONS.filter((promo)=>promo.featured)[0]}
       leader={this.state.LEADERS.filter((leader)=>leader.featured)[0]}
        />
      )
    }
    var dishWithId=({match})=>{
      console.log("testy")
      return(
      <Menudetail dish={this.state.dishes.filter((dish)=>dish.id==parseInt(match.params.dishId,10))[0]}
      comments={this.state.COMMENTS.filter((comment) => comment.dishId === parseInt(match.params.dishId,10))} />
      )
    }
  return (
    <div >
     <Header/>
      <Switch>
        <Route path="/home"  component={homepage}/>
{console.log("test1")}

        <Route exact path="/menu"  component={()=>
          <Menu dishes={this.state.dishes}/>
         
        }/>
           <Route path="/contact"  component={()=>
          <Contact/>
         
        }/>
       <Route path="/menu/:dishId" component={dishWithId}/>
      
        </Switch> 
    
   
    </div>
  );
  }
}

export default Main;
